import React from 'react';
import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom';
import Landing from './components/Landing.js';
import ClientDashboard from './components/ClientDashboard.js';
import TrainerDashboard from './components/TrainerDashboard.js';

const App = () => {
  // TODO: Implement authentication logic
  const isAuthenticated = false;
  const userRole = 'client'; // or 'trainer'

  return (
    <Router>
      <div style={{ backgroundColor: 'rgb(25, 25, 25)', color: 'white', minHeight: '100vh' }}>
        <Switch>
          <Route exact path="/" component={Landing} />
          <Route
            path="/dashboard"
            render={() => {
              if (!isAuthenticated) {
                return <Redirect to="/" />;
              }
              return userRole === 'client' ? <ClientDashboard /> : <TrainerDashboard />;
            }}
          />
          {/* Add more routes as needed */}
        </Switch>
      </div>
    </Router>
  );
};

export default App;
